import React, { useState, useEffect } from 'react';

interface Circuit {
  circuitName: string;
  Location: {
    locality: string;
    country: string;
  };
}

interface Race {
  round: string;
  raceName: string;
  date: string;
  time: string;
  Circuit: Circuit;
}

const Calendar: React.FC = () => {
  const [allRaces, setAllRaces] = useState<Race[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [activeView, setActiveView] = useState<'upcoming' | 'previous'>('upcoming');

  useEffect(() => {
    fetchRaceSchedule();
  }, []);

  const fetchRaceSchedule = async () => {
    console.log('🏎️ Loading F1 Calendar from APIs...');
    
    // Try multiple APIs in sequence
    const apis = [
      'https://api.jolpi.ca/ergast/f1/2025.json',
      'http://ergast.com/api/f1/2025.json',
      'https://ergast.com/api/f1/2025.json'
    ];
    
    for (const apiUrl of apis) {
      try {
        console.log(`📡 Trying API: ${apiUrl}`);
        const response = await fetch(apiUrl);
        if (response.ok) {
          const data = await response.json();
          const races = data.MRData?.RaceTable?.Races;
          if (races && races.length > 0) {
            console.log(`✅ API Success! ${races.length} races loaded from ${apiUrl}`);
            setAllRaces(races);
            setLoading(false);
            return;
          }
        }
      } catch (error) {
        console.warn(`⚠️ API failed: ${apiUrl}`, error);
      }
    }
    
    // All APIs failed, use fallback data
    console.log('⚠️ All APIs failed, using fallback 2025 calendar');
    const fallbackRaces = get2025Calendar();
    setAllRaces(fallbackRaces);
    setLoading(false);
  };

  const get2025Calendar = (): Race[] => {
    return [
      { round: '1', raceName: 'Australian Grand Prix', date: '2025-03-16', time: '05:00:00Z', Circuit: { circuitName: 'Albert Park Circuit', Location: { locality: 'Melbourne', country: 'Australia' } } },
      { round: '2', raceName: 'Chinese Grand Prix', date: '2025-03-23', time: '07:00:00Z', Circuit: { circuitName: 'Shanghai International Circuit', Location: { locality: 'Shanghai', country: 'China' } } },
      { round: '3', raceName: 'Japanese Grand Prix', date: '2025-04-06', time: '05:00:00Z', Circuit: { circuitName: 'Suzuka Circuit', Location: { locality: 'Suzuka', country: 'Japan' } } },
      { round: '4', raceName: 'Bahrain Grand Prix', date: '2025-04-13', time: '15:00:00Z', Circuit: { circuitName: 'Bahrain International Circuit', Location: { locality: 'Sakhir', country: 'Bahrain' } } },
      { round: '5', raceName: 'Saudi Arabian Grand Prix', date: '2025-04-20', time: '17:00:00Z', Circuit: { circuitName: 'Jeddah Corniche Circuit', Location: { locality: 'Jeddah', country: 'Saudi Arabia' } } },
      { round: '6', raceName: 'Miami Grand Prix', date: '2025-05-04', time: '19:30:00Z', Circuit: { circuitName: 'Miami International Autodrome', Location: { locality: 'Miami', country: 'USA' } } },
      { round: '7', raceName: 'Emilia Romagna Grand Prix', date: '2025-05-18', time: '13:00:00Z', Circuit: { circuitName: 'Autodromo Enzo e Dino Ferrari', Location: { locality: 'Imola', country: 'Italy' } } },
      { round: '8', raceName: 'Monaco Grand Prix', date: '2025-05-25', time: '13:00:00Z', Circuit: { circuitName: 'Circuit de Monaco', Location: { locality: 'Monte Carlo', country: 'Monaco' } } },
      { round: '9', raceName: 'Spanish Grand Prix', date: '2025-06-01', time: '13:00:00Z', Circuit: { circuitName: 'Circuit de Barcelona-Catalunya', Location: { locality: 'Barcelona', country: 'Spain' } } },
      { round: '10', raceName: 'Canadian Grand Prix', date: '2025-06-15', time: '18:00:00Z', Circuit: { circuitName: 'Circuit Gilles Villeneuve', Location: { locality: 'Montreal', country: 'Canada' } } },
      { round: '11', raceName: 'Austrian Grand Prix', date: '2025-06-29', time: '13:00:00Z', Circuit: { circuitName: 'Red Bull Ring', Location: { locality: 'Spielberg', country: 'Austria' } } },
      { round: '12', raceName: 'British Grand Prix', date: '2025-07-06', time: '14:00:00Z', Circuit: { circuitName: 'Silverstone Circuit', Location: { locality: 'Silverstone', country: 'UK' } } },
      { round: '13', raceName: 'Belgian Grand Prix', date: '2025-07-27', time: '13:00:00Z', Circuit: { circuitName: 'Circuit de Spa-Francorchamps', Location: { locality: 'Spa', country: 'Belgium' } } },
      { round: '14', raceName: 'Hungarian Grand Prix', date: '2025-08-03', time: '13:00:00Z', Circuit: { circuitName: 'Hungaroring', Location: { locality: 'Budapest', country: 'Hungary' } } },
      { round: '15', raceName: 'Dutch Grand Prix', date: '2025-08-31', time: '13:00:00Z', Circuit: { circuitName: 'Circuit Zandvoort', Location: { locality: 'Zandvoort', country: 'Netherlands' } } },
      { round: '16', raceName: 'Italian Grand Prix', date: '2025-09-07', time: '13:00:00Z', Circuit: { circuitName: 'Autodromo Nazionale di Monza', Location: { locality: 'Monza', country: 'Italy' } } },
      { round: '17', raceName: 'Azerbaijan Grand Prix', date: '2025-09-21', time: '11:00:00Z', Circuit: { circuitName: 'Baku City Circuit', Location: { locality: 'Baku', country: 'Azerbaijan' } } },
      { round: '18', raceName: 'Singapore Grand Prix', date: '2025-10-05', time: '12:00:00Z', Circuit: { circuitName: 'Marina Bay Street Circuit', Location: { locality: 'Singapore', country: 'Singapore' } } },
      { round: '19', raceName: 'United States Grand Prix', date: '2025-10-19', time: '19:00:00Z', Circuit: { circuitName: 'Circuit of the Americas', Location: { locality: 'Austin', country: 'USA' } } },
      { round: '20', raceName: 'Mexico City Grand Prix', date: '2025-10-26', time: '20:00:00Z', Circuit: { circuitName: 'Autódromo Hermanos Rodríguez', Location: { locality: 'Mexico City', country: 'Mexico' } } },
      { round: '21', raceName: 'São Paulo Grand Prix', date: '2025-11-09', time: '17:00:00Z', Circuit: { circuitName: 'Autódromo José Carlos Pace', Location: { locality: 'São Paulo', country: 'Brazil' } } },
      { round: '22', raceName: 'Las Vegas Grand Prix', date: '2025-11-22', time: '06:00:00Z', Circuit: { circuitName: 'Las Vegas Street Circuit', Location: { locality: 'Las Vegas', country: 'USA' } } },
      { round: '23', raceName: 'Qatar Grand Prix', date: '2025-11-30', time: '16:00:00Z', Circuit: { circuitName: 'Losail International Circuit', Location: { locality: 'Lusail', country: 'Qatar' } } },
      { round: '24', raceName: 'Abu Dhabi Grand Prix', date: '2025-12-07', time: '13:00:00Z', Circuit: { circuitName: 'Yas Marina Circuit', Location: { locality: 'Abu Dhabi', country: 'UAE' } } }
    ];
  };

  const getCountryFlag = (country: string): string => {
    const flagMap: { [key: string]: string } = {
      'Australia': '🇦🇺', 'China': '🇨🇳', 'Japan': '🇯🇵', 'Bahrain': '🇧🇭',
      'Saudi Arabia': '🇸🇦', 'USA': '🇺🇸', 'Italy': '🇮🇹', 'Monaco': '🇲🇨',
      'Spain': '🇪🇸', 'Canada': '🇨🇦', 'Austria': '🇦🇹', 'UK': '🇬🇧',
      'Hungary': '🇭🇺', 'Belgium': '🇧🇪', 'Netherlands': '🇳🇱', 'Azerbaijan': '🇦🇿',
      'Singapore': '🇸🇬', 'Mexico': '🇲🇽', 'Brazil': '🇧🇷', 'Qatar': '🇶🇦',
      'UAE': '🇦🇪'
    };
    return flagMap[country] || '🏁';
  };

  const formatRaceDate = (dateStr: string, timeStr: string): string => {
    const date = new Date(dateStr + 'T' + timeStr);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric' 
    });
  };

  const getRacesByView = () => {
    const now = new Date();
    return allRaces.filter(race => {
      const raceDate = new Date(race.date);
      return activeView === 'upcoming' ? raceDate >= now : raceDate < now;
    }).sort((a, b) => {
      const dateA = new Date(a.date);
      const dateB = new Date(b.date);
      return activeView === 'upcoming' 
        ? dateA.getTime() - dateB.getTime()
        : dateB.getTime() - dateA.getTime();
    });
  };

  const filteredRaces = getRacesByView();

  if (loading) {
    return (
      <div className="calendar-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <h2>Loading Calendar...</h2>
        </div>
      </div>
    );
  }

  return (
    <main>
      <section className="calendar-section">
        <div className="section-header">
          <div className="section-badge">SEASON SCHEDULE</div>
          <h2 className="section-title">Race Calendar 2025 <span className="live-badge">🔴 LIVE</span></h2>
          <p className="section-subtitle">Complete schedule of all Grand Prix races</p>
          <div className="last-updated" style={{ textAlign: 'center', color: 'var(--gray-400)', fontSize: '0.85rem', marginTop: '0.5rem' }}>
            <span>⟳ Updates automatically every 10 minutes</span>
          </div>
          
          <div className="calendar-toggle">
            <button 
              className={`toggle-btn ${activeView === 'upcoming' ? 'active' : ''}`}
              onClick={() => setActiveView('upcoming')}
            >
              <span className="toggle-icon">🔮</span>
              <span>Upcoming Races</span>
            </button>
            <button 
              className={`toggle-btn ${activeView === 'previous' ? 'active' : ''}`}
              onClick={() => setActiveView('previous')}
            >
              <span className="toggle-icon">✓</span>
              <span>Previous Races</span>
            </button>
          </div>
        </div>
        <div className="races-grid">
          {filteredRaces.length === 0 ? (
            <div className="no-races">
              <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>
                {activeView === 'upcoming' ? '🏁' : '📜'}
              </div>
              <h3>No {activeView === 'upcoming' ? 'Upcoming' : 'Previous'} Races</h3>
              <p>
                {activeView === 'upcoming' 
                  ? 'The season calendar will be announced soon!' 
                  : 'The season hasn\'t started yet!'}
              </p>
            </div>
          ) : (
            filteredRaces.map((race, index) => {
              const isNext = activeView === 'upcoming' && index === 0;
              const isRecent = activeView === 'previous' && index === 0;

              return (
                <div 
                  key={race.round} 
                  className={`race-card ${isNext ? 'race-next' : ''} ${activeView === 'previous' ? 'race-past' : ''} ${isRecent ? 'race-recent' : ''}`}
                >
                  {isNext && <div className="next-badge">⚡ NEXT RACE</div>}
                  {isRecent && <div className="past-badge">✓ LATEST RACE</div>}
                  {activeView === 'previous' && !isRecent && <div className="past-badge">✓ COMPLETED</div>}
                  
                  <div className="race-flag">{getCountryFlag(race.Circuit.Location.country)}</div>
                  <div className="race-info">
                    <div className="race-round">ROUND {race.round}</div>
                    <h3>{race.raceName}</h3>
                    <p className="race-circuit">{race.Circuit.circuitName}</p>
                    <div className="race-date">
                      <span className="date-icon">📅</span>
                      <span>{formatRaceDate(race.date, race.time)}</span>
                    </div>
                    <div className="race-stats">
                      <div className="stat-item-small">
                        <span className="label">Location</span>
                        <span className="value">{race.Circuit.Location.locality}</span>
                      </div>
                      <div className="stat-item-small">
                        <span className="label">Round</span>
                        <span className="value">{race.round}/{allRaces.length}</span>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </section>
    </main>
  );
};

export default Calendar;
