import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/standings" element={<ComingSoon title="Standings" />} />
            <Route path="/calendar" element={<ComingSoon title="Calendar" />} />
            <Route path="/race-results" element={<ComingSoon title="Race Results" />} />
            <Route path="/drivers" element={<ComingSoon title="Drivers" />} />
            <Route path="/teams" element={<ComingSoon title="Teams" />} />
            <Route path="/driver/:id" element={<ComingSoon title="Driver Profile" />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

// Temporary Coming Soon component
const ComingSoon: React.FC<{ title: string }> = ({ title }) => (
  <div style={{
    minHeight: '60vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: '4rem 2rem'
  }}>
    <h1 style={{
      fontSize: '3rem',
      color: 'var(--primary-red)',
      marginBottom: '1rem',
      fontFamily: 'var(--font-display)'
    }}>
      üèéÔ∏è {title}
    </h1>
    <p style={{
      fontSize: '1.3rem',
      color: 'var(--gray-400)',
      maxWidth: '600px'
    }}>
      This page is under construction. We're working hard to bring you the best F1 experience!
    </p>
  </div>
);

export default App;
